function wsys.affTags() if next(wsys.aff) then local s = {} for k,v in pairs(wsys.aff) do if k == "prone" then s[#s+1] = "<orchid>PR" --kelp stack coloring elseif k=="asthma" or k=="sensitivity" or k=="weariness" or k=="clumsiness" or k=="hypochondria" or k=="healthleech" then s[#s+1] = "<chartreuse>"..wsys.tb.aff_short[k] --goldenseal stack coloring elseif k=="dissonance" or k=="dizziness" or k=="epilepsy" or k=="impatience" or k=="shyness" or k=="stupidity" then s[#s+1] = "<yellow>"..wsys.tb.aff_short[k] --ginseng stack elseif k=="addiction" or k=="darkshade" or k=="haemophilia" or k=="lethargy" or k=="nausea" or k=="scytherus" then s[#s+1] = "<dark_goldenrod>"..wsys.tb.aff_short[k] --broken limbs elseif k=="damagedhead" or k=="damagedleftarm" or k=="damagedleftleg" or k=="damagedrightarm" or k=="damagedrightleg" or k=="mildtrauma" or k=="carm" or k=="brokenleftarm" or k=="brokenleftleg" or k=="clef" or k=="climb" or k=="brokenrightarm" or k=="brokenrightleg" or k=="mangledhead" or k=="mangledleftarm" or k=="mangledleftleg" or k=="mangledrightarm" or k=="mangledrightleg" or k=="serioustrauma" then s[#s+1] = "<brown>"..wsys.tb.aff_short[k] --all other affs get default coloring elseif k:find("tempered") then s[#s+1] = "<MediumPurple>"..wsys.tb.aff_short[k]() elseif k=="torntendons" or k=="wristfractures" or k=="crackedribs" or k=="skullfractures" then s[#s+1] = "<medium_violet_red>"..wsys.tb.aff_short[k]() elseif k == "bleeding" then if wsys.bleeding > 50 then s[#s+1] = "<maroon>"..wsys.tb.aff_short[k]() end elseif k == "burning" then s[#s+1] = wsys.tb.aff_short[k]() elseif not wsys.tb.aff_short[k] then s[#s+1] = "<IndianRed>"..k else s[#s+1] = "<SlateGrey>"..wsys.tb.aff_short[k] end end return #s > 0 and ("<IndianRed>["..table.concat(s, " ").."<IndianRed>]") or "" -- don't display just [] b/c of bleeding else return "" end end
